import { useState } from "react";
import axiosInstance from "../api";
import { getAuthHeader } from "../auth";

export default function AddDoctor({ onDoctorAdded }) {
  const [name, setName] = useState("");
  const [specialization, setSpecialization] = useState("");
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");
    setLoading(true);
    try {
      const res = await axiosInstance.post(
        "doctors/",
        { name, specialization },
        { headers: getAuthHeader() }
      );
      setMessage("Doctor added successfully!");
      setName("");
      setSpecialization("");
      if (onDoctorAdded) onDoctorAdded(); // refresh doctor list
    } catch (err) {
      console.error(err);
      setMessage("Failed to add doctor.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="p-4 bg-white shadow rounded mb-6">
      <h2 className="text-xl font-bold mb-4">Add Doctor</h2>
      {message && <p className="mb-2 text-green-600">{message}</p>}
      <input
        type="text"
        placeholder="Name"
        value={name}
        onChange={(e) => setName(e.target.value)}
        required
        className="w-full border p-2 mb-3 rounded"
      />
      <input
        type="text"
        placeholder="Specialization"
        value={specialization}
        onChange={(e) => setSpecialization(e.target.value)}
        required
        className="w-full border p-2 mb-3 rounded"
      />
      <button
        type="submit"
        disabled={loading}
        className="bg-blue-600 text-white px-4 py-2 rounded"
      >
        {loading ? "Adding..." : "Add Doctor"}
      </button>
    </form>
  );
}
