import React, { useState, useEffect } from "react";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";

import Dashboard from "./components/Dashboard";
import Home from "./pages/Home";
import Doctors from "./pages/Doctors";
import Patients from "./pages/Patients";
import Appointments from "./pages/Appointments";
import Prescriptions from "./pages/Prescriptions";
import Auth from "./components/Auth"; // Ensure path matches your folder structure

export default function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [role, setRole] = useState(""); // Logged-in user role

  // -------------------------
  // Check authentication on load
  // -------------------------
  useEffect(() => {
    const token = localStorage.getItem("access_token");
    const savedRole = localStorage.getItem("role");
    if (token) {
      setIsAuthenticated(true);
      setRole(savedRole || "");
    }
  }, []);

  // -------------------------
  // Get dashboard URL from localStorage or role
  // -------------------------
  const getDashboardUrl = () => {
    const url = localStorage.getItem("dashboard_url");
    if (url) return url;

    switch (role) {
      case "doctor":
        return "/doctors";
      case "patient":
        return "/patients";
      case "agent":
        return "/home";
      case "management":
        return "/home";
      case "admin":
        return "/home";
      default:
        return "/home";
    }
  };

  // -------------------------
  // Render main pages
  // -------------------------
  const renderPage = (page) => {
    switch (page) {
      case "home":
        return <Home />;
      case "doctors":
        return <Doctors />;
      case "patients":
        return <Patients />;
      case "appointments":
        return <Appointments />;
      case "prescriptions":
        return <Prescriptions />;
      default:
        return <Home />;
    }
  };

  // -------------------------
  // Show login/register if not authenticated
  // -------------------------
  if (!isAuthenticated) {
    return <Auth setIsAuthenticated={setIsAuthenticated} setRole={setRole} />;
  }

  // -------------------------
  // Render dashboard with routes
  // -------------------------
  return (
    <BrowserRouter>
      <Dashboard setIsAuthenticated={setIsAuthenticated}>
        <Routes>
          {/* Main pages */}
          <Route path="/" element={renderPage("home")} />
          <Route path="/home" element={<Home />} />
          <Route path="/doctors" element={<Doctors />} />
          <Route path="/patients" element={<Patients />} />
          <Route path="/appointments" element={<Appointments />} />
          <Route path="/prescriptions" element={<Prescriptions />} />

          {/* Redirect to role-based dashboard */}
          <Route path="/dashboard" element={<Navigate to={getDashboardUrl()} replace />} />

          {/* Catch-all unknown routes */}
          <Route path="*" element={<Navigate to={getDashboardUrl()} replace />} />
        </Routes>
      </Dashboard>
    </BrowserRouter>
  );
}
