import React, { useState } from "react";

const BASE_URL = "http://localhost:8000/";

export default function Auth({ setPage }) {
  const [mode, setMode] = useState("login"); // "login" | "register"
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("patient"); // role dropdown
  const [message, setMessage] = useState("");

  // ---------------------------
  // API Functions
  // ---------------------------
  const loginUser = async (username, password, role) => {
    const response = await fetch(`${BASE_URL}api/login/`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ username, password, role }),
    });
    return response.json();
  };

  const registerUser = async (username, email, password, role) => {
    const response = await fetch(`${BASE_URL}api/auth/register/`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ username, email, password, role }),
    });
    return response.json();
  };

  const registerDoctorProfile = async (userId) => {
    const response = await fetch(`${BASE_URL}api/doctors/register/`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        user_id: userId,
        specialization: "General",
        consultation_fee: 0,
      }),
    });
    return response.json();
  };

  // ---------------------------
  // Form submit
  // ---------------------------
  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");

    try {
      if (mode === "login") {
        const data = await loginUser(username, password, role);
        if (data.error) throw new Error(data.error);

        localStorage.setItem("access_token", data.token);
        localStorage.setItem("dashboard_url", data.dashboard_url);
        localStorage.setItem("username", username);

        setPage("home");
      } else {
        // Register user
        const regData = await registerUser(username, email, password, role);
        if (regData.error) throw new Error(regData.error);

        // If doctor, register doctor profile
        if (role === "doctor") {
          const docData = await registerDoctorProfile(regData.user.id);
          if (docData.error) throw new Error(docData.error || "Doctor registration failed");
        }

        // Auto-login
        const data = await loginUser(username, password, role);
        if (data.error) throw new Error(data.error);

        localStorage.setItem("access_token", data.token);
        localStorage.setItem("dashboard_url", data.dashboard_url);
        localStorage.setItem("username", username);

        setMessage("Registration successful! Logged in automatically.");
        setPage("home");
      }
    } catch (err) {
      console.error(err);
      setMessage(err.message || "Something went wrong");
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <form onSubmit={handleSubmit} className="bg-white p-6 shadow-lg rounded-lg w-80">
        <h2 className="text-xl font-bold mb-4">{mode === "login" ? "Login" : "Register"}</h2>
        {message && <p className="text-red-500 mb-2">{message}</p>}

        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          className="w-full border p-2 mb-3 rounded"
          required
        />

        {mode === "register" && (
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="w-full border p-2 mb-3 rounded"
            required
          />
        )}

        <select
          value={role}
          onChange={(e) => setRole(e.target.value)}
          className="w-full border p-2 mb-3 rounded bg-white"
        >
          <option value="doctor">Doctor</option>
          <option value="patient">Patient</option>
          <option value="agent">Agent/Tennent</option>
          <option value="management">Management</option>
          <option value="admin">Admin</option>
        </select>

        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="w-full border p-2 mb-3 rounded"
          required
        />

        <button
          type="submit"
          className="bg-blue-600 text-white w-full py-2 rounded hover:bg-blue-700 transition"
        >
          {mode === "login" ? "Login" : "Register"}
        </button>

        <p className="text-sm text-center mt-3">
          {mode === "login" ? (
            <>
              Donâ€™t have an account?{" "}
              <button type="button" onClick={() => setMode("register")} className="text-blue-600 underline">
                Register
              </button>
            </>
          ) : (
            <>
              Already have an account?{" "}
              <button type="button" onClick={() => setMode("login")} className="text-blue-600 underline">
                Login
              </button>
            </>
          )}
        </p>
      </form>
    </div>
  );
}
