import React, { useState } from "react";
import axiosInstance from "./api"; // api.js path অনুযায়ী adjust করুন

export default function Auth({ setPage }) {
  const [mode, setMode] = useState("login"); // "login" | "register"
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("patient"); // ✅ New state for role
  const [message, setMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");

    try {
      if (mode === "login") {
        // --- LOGIN API ---
        await loginUser(username, password, role);
      } else {
        // --- REGISTER API ---
        const response = await axiosInstance.post("auth/register/", {
          username,
          password,
          email,
          role,
        });

        // Auto-login after registration
        await loginUser(username, password, role);
        setMessage("Registration successful! Logged in automatically.");
      }
    } catch (err) {
      setMessage(err.response?.data?.error || err.message || "Something went wrong");
    }
  };

  const loginUser = async (username, password, role) => {
    try {
      const response = await axiosInstance.post("login/", {
        username,
        password,
        role,
      });

      const data = response.data;

      // Save token for future API calls
      localStorage.setItem("access_token", data.token); // interceptor জন্য
      localStorage.setItem("dashboard_url", data.dashboard_url);
      localStorage.setItem("username", username);

      // Redirect to role-based dashboard
      setPage("home");
    } catch (err) {
      throw new Error(err.response?.data?.error || "Login failed");
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <form
        onSubmit={handleSubmit}
        className="bg-white p-6 shadow-lg rounded-lg w-80"
      >
        <h2 className="text-xl font-bold mb-4">
          {mode === "login" ? "Login" : "Register"}
        </h2>

        {message && <p className="text-red-500 mb-2">{message}</p>}

        {/* Username */}
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          className="w-full border p-2 mb-3 rounded"
          required
        />

        {/* Email field only for register */}
        {mode === "register" && (
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="w-full border p-2 mb-3 rounded"
            required
          />
        )}

        {/* Role selection */}
        <select
          value={role}
          onChange={(e) => setRole(e.target.value)}
          className="w-full border p-2 mb-3 rounded bg-white"
        >
          <option value="doctor">Doctor</option>
          <option value="patient">Patient</option>
          <option value="agent">Agent/Tennent</option>
          <option value="management">Management</option>
          <option value="admin">Admin</option>
        </select>

        {/* Password */}
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="w-full border p-2 mb-3 rounded"
          required
        />

        <button
          type="submit"
          className="bg-blue-600 text-white w-full py-2 rounded hover:bg-blue-700 transition"
        >
          {mode === "login" ? "Login" : "Register"}
        </button>

        {/* Toggle between login/register */}
        <p className="text-sm text-center mt-3">
          {mode === "login" ? (
            <>
              Don’t have an account?{" "}
              <button
                type="button"
                onClick={() => setMode("register")}
                className="text-blue-600 underline"
              >
                Register
              </button>
            </>
          ) : (
            <>
              Already have an account?{" "}
              <button
                type="button"
                onClick={() => setMode("login")}
                className="text-blue-600 underline"
              >
                Login
              </button>
            </>
          )}
        </p>
      </form>
    </div>
  );
}
