import { useEffect, useMemo, useState } from "react";
import axiosInstance from "../api";
import { getAuthHeader } from "../auth";

function errText(err) {
  if (err?.response) {
    const { status, statusText, data, config } = err.response;
    const details = typeof data === "string" ? data : JSON.stringify(data);
    return `HTTP ${status} ${statusText} @ ${config?.url} → ${details}`;
  }
  return err?.message || "Network error";
}

export default function Doctors() {
  const [doctors, setDoctors] = useState([]);
  const [loading, setLoading] = useState(true);
  const [doctorsError, setDoctorsError] = useState(null);

  const [users, setUsers] = useState([]);
  const [usersLoading, setUsersLoading] = useState(false);
  const [usersError, setUsersError] = useState(null);

  const [q, setQ] = useState("");

  // form
  const [userId, setUserId] = useState(""); // required
  const [formData, setFormData] = useState({
    specialization: "",
    name: "",
    email: "",
    phone: "",
  });

  const [submitError, setSubmitError] = useState(null);
  const [success, setSuccess] = useState(null);

  const extractList = (data) => (Array.isArray(data) ? data : data?.results || []);

  const fetchDoctors = async () => {
    try {
      setLoading(true);
      setDoctorsError(null);
      const res = await axiosInstance.get("doctors/", { headers: getAuthHeader() });
      setDoctors(extractList(res.data));
    } catch (err) {
      console.error("fetchDoctors:", err);
      setDoctorsError(errText(err));
    } finally {
      setLoading(false);
    }
  };

  const fetchUsers = async () => {
    try {
      setUsersLoading(true);
      setUsersError(null);
      let url = "users/";
      const all = [];
      while (url) {
        const { data } = await axiosInstance.get(url, { headers: getAuthHeader() });
        all.push(...extractList(data));
        if (!Array.isArray(data) && data?.next) {
          try {
            const u = new URL(data.next);
            url = u.pathname.replace(/^\/api\//, "") + (u.search || "");
          } catch {
            url = data.next;
          }
        } else {
          url = null;
        }
      }
      setUsers(all);
    } catch (err) {
      console.error("fetchUsers:", err);
      setUsersError(errText(err)); // আগের generic "Failed to load users." এর বদলে ডিটেইলস
    } finally {
      setUsersLoading(false);
    }
  };

  useEffect(() => {
    fetchDoctors();
    fetchUsers();
  }, []);

  const filteredUsers = useMemo(() => {
    if (!q) return users;
    const s = q.toLowerCase();
    return users.filter(
      (u) =>
        (u.username && u.username.toLowerCase().includes(s)) ||
        (u.email && u.email.toLowerCase().includes(s)) ||
        String(u.id).includes(s)
    );
  }, [q, users]);

  const handleChange = (e) =>
    setFormData((prev) => ({ ...prev, [e.target.name]: e.target.value }));

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitError(null);
    setSuccess(null);

    if (!userId || !formData.specialization.trim()) {
      setSubmitError("User ID এবং Specialization প্রয়োজন।");
      return;
    }

    try {
      const payload = {
        user_id: Number(userId), // serializers.py -> user_id (write_only, source='user')
        specialization: formData.specialization.trim(),
        phone: formData.phone || "",
        name: formData.name || undefined,
        email: formData.email || undefined,
      };

      await axiosInstance.post("doctors/register/", payload, {
        headers: getAuthHeader(),
      });

      setSuccess("Doctor added successfully!");
      setFormData({ specialization: "", name: "", email: "", phone: "" });
      setUserId("");
      await fetchDoctors();
    } catch (err) {
      console.error("submit:", err);
      setSubmitError(errText(err));
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-6 text-gray-800">Doctors</h1>

      {/* Users load errors (ডিটেইল সহ) */}
      {usersError && (
        <div className="mb-4 rounded border border-red-300 bg-red-50 p-3 text-red-700">
          <div className="font-semibold">Failed to load users</div>
          <div className="text-sm break-words whitespace-pre-wrap">{usersError}</div>
          <div className="text-sm text-gray-600 mt-1">
            টিপ: টোকেন / পারমিশন / CORS / URL ডাবল-চেক করুন।
          </div>
        </div>
      )}

      {/* Add Doctor Form */}
      <form onSubmit={handleSubmit} className="mb-6 space-y-4">
        {submitError && <p className="text-red-500 break-words">{submitError}</p>}
        {success && <p className="text-green-500">{success}</p>}

        {/* User search + select; লোড ফেল করলে fallback ইনপুট */}
        <div>
          <label className="block font-medium mb-1">Search users</label>
          <input
            type="text"
            placeholder="username/email/id"
            value={q}
            onChange={(e) => setQ(e.target.value)}
            className="border p-2 rounded w-full mb-2"
          />

          {!usersError ? (
            <select
              value={userId}
              onChange={(e) => setUserId(e.target.value)}
              className="border p-2 rounded w-full"
              disabled={usersLoading}
            >
              <option value="">{usersLoading ? "Loading users..." : "Select a user"}</option>
              {filteredUsers.map((u) => (
                <option key={u.id} value={u.id}>
                  {(u.username || u.email || "user")} #{u.id}
                </option>
              ))}
            </select>
          ) : (
            <input
              type="number"
              placeholder="Enter user_id manually (fallback)"
              value={userId}
              onChange={(e) => setUserId(e.target.value)}
              className="border p-2 rounded w-full"
            />
          )}
        </div>

        <input
          type="text"
          name="specialization"
          placeholder="Specialization"
          value={formData.specialization}
          onChange={handleChange}
          className="border p-2 rounded w-full"
        />

        <input
          type="text"
          name="name"
          placeholder="Doctor Name (optional)"
          value={formData.name}
          onChange={handleChange}
          className="border p-2 rounded w-full"
        />
        <input
          type="email"
          name="email"
          placeholder="Email (optional)"
          value={formData.email}
          onChange={handleChange}
          className="border p-2 rounded w-full"
        />
        <input
          type="text"
          name="phone"
          placeholder="Phone (optional)"
          value={formData.phone}
          onChange={handleChange}
          className="border p-2 rounded w-full"
        />

        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded disabled:opacity-60"
          disabled={!userId || !formData.specialization.trim()}
        >
          Add Doctor
        </button>
      </form>

      {/* Doctors Table */}
      {loading ? (
        <div className="flex justify-center items-center h-64">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500"></div>
        </div>
      ) : doctorsError ? (
        <div className="rounded border border-red-300 bg-red-50 p-3 text-red-700">
          <div className="font-semibold">Failed to load doctors</div>
          <div className="text-sm break-words whitespace-pre-wrap">{doctorsError}</div>
        </div>
      ) : doctors.length === 0 ? (
        <p className="text-gray-500 text-lg">No doctors found.</p>
      ) : (
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white rounded-lg shadow-md border border-gray-200">
            <thead className="bg-blue-600 text-white">
              <tr>
                <th className="px-6 py-3 text-left font-medium">ID</th>
                <th className="px-6 py-3 text-left font-medium">Name</th>
                <th className="px-6 py-3 text-left font-medium">Specialization</th>
                <th className="px-6 py-3 text-left font-medium">Email</th>
                <th className="px-6 py-3 text-left font-medium">Phone</th>
              </tr>
            </thead>
            <tbody>
              {doctors.map((doc) => (
                <tr key={doc.id} className="border-b hover:bg-blue-50 transition-colors">
                  <td className="px-6 py-3">{doc.id}</td>
                  <td className="px-6 py-3">{doc.name}</td>
                  <td className="px-6 py-3">{doc.specialization}</td>
                  <td className="px-6 py-3">{doc.email}</td>
                  <td className="px-6 py-3">{doc.phone}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}
