import React, { useEffect, useState } from "react";

function StatCard({ title, count, bg }) {
  return (
    <div
      className={`${bg} text-white p-6 rounded-lg shadow-md transform hover:scale-105 transition-transform`}
    >
      <h2 className="text-xl font-semibold">{title}</h2>
      <p className="text-3xl font-bold mt-2">{count}</p>
    </div>
  );
}

export default function Home() {
  const [stats, setStats] = useState([
    { title: "Doctors", count: 0, bg: "bg-blue-600" },
    { title: "Patients", count: 0, bg: "bg-green-600" },
    { title: "Appointments", count: 0, bg: "bg-yellow-500" },
    { title: "Prescriptions", count: 0, bg: "bg-red-500" },
  ]);

  useEffect(() => {
    async function fetchStats() {
      try {
        const response = await fetch("http://localhost:8000/api/stats/");
        if (!response.ok) throw new Error("Failed to fetch stats");
        const data = await response.json();
        setStats([
          { title: "Doctors", count: data.doctors, bg: "bg-blue-600" },
          { title: "Patients", count: data.patients, bg: "bg-green-600" },
          { title: "Appointments", count: data.appointments, bg: "bg-yellow-500" },
          { title: "Prescriptions", count: data.prescriptions, bg: "bg-red-500" },
        ]);
      } catch (err) {
        console.error(err);
      }
    }

    fetchStats();
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">Welcome to Doctor APS</h1>
      <p className="text-gray-600 mb-6">
        Manage your doctors, patients, appointments, and prescriptions.
      </p>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {stats.map((stat) => (
          <StatCard key={stat.title} title={stat.title} count={stat.count} bg={stat.bg} />
        ))}
      </div>
    </div>
  );
}
