import { useState, useEffect } from "react";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";

import Dashboard from "./components/Dashboard";
import Home from "./pages/Home";
import Doctors from "./pages/Doctors";
import Patients from "./pages/Patients";
import Appointments from "./pages/Appointments";
import Prescriptions from "./pages/Prescriptions";
import Auth from "./pages/Auth"; // <-- Updated to merged Auth.jsx

export default function App() {
  const [page, setPage] = useState("login"); // "login" or "home"
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Check if user is already logged in
  useEffect(() => {
    const token = localStorage.getItem("access_token");
    if (token) {
      setIsAuthenticated(true);
      setPage("home");
    }
  }, []);

  const renderPage = () => {
    switch (page) {
      case "login":
        return <Auth setPage={(p) => { setPage(p); setIsAuthenticated(true); }} />;
      case "home":
        return <Home />;
      case "doctors":
        return <Doctors />;
      case "patients":
        return <Patients />;
      case "appointments":
        return <Appointments />;
      case "prescriptions":
        return <Prescriptions />;
      default:
        return <Home />;
    }
  };

  const dashboardUrl = localStorage.getItem("dashboard_url") || "/home";

  // If not logged in, always show Auth page
  if (!isAuthenticated) return <Auth setPage={(p) => { setPage(p); setIsAuthenticated(true); }} />;

  return (
    <BrowserRouter>
      <Dashboard setPage={(p) => { setPage(p); setIsAuthenticated(p === "home"); }}>
        <Routes>
          <Route path="/" element={renderPage()} />
          <Route path="/home" element={<Home />} />
          <Route path="/doctors" element={<Doctors />} />
          <Route path="/patients" element={<Patients />} />
          <Route path="/appointments" element={<Appointments />} />
          <Route path="/prescriptions" element={<Prescriptions />} />

          {/* Role-based redirect */}
          <Route path="/dashboard" element={<Navigate to={dashboardUrl} replace />} />

          {/* Redirect unknown routes to dashboard/home */}
          <Route path="*" element={<Navigate to={dashboardUrl} replace />} />
        </Routes>
      </Dashboard>
    </BrowserRouter>
  );
}
