// src/Sidebar.jsx
import { Stethoscope, Users, CalendarDays, FileText } from "lucide-react";
import { Link, useLocation } from "react-router-dom";

export default function Sidebar() {
  const location = useLocation();
  const menuItems = [
    { name: "Doctors", icon: <Stethoscope className="w-5 h-5" />, path: "/doctors" },
    { name: "Patients", icon: <Users className="w-5 h-5" />, path: "/patients" },
    { name: "Appointments", icon: <CalendarDays className="w-5 h-5" />, path: "/appointments" },
    { name: "Prescriptions", icon: <FileText className="w-5 h-5" />, path: "/prescriptions" },
  ];

  return (
    <aside className="w-64 bg-gray-800 text-gray-100 min-h-screen p-5">
      <ul className="space-y-4">
        {menuItems.map((item) => (
          <li key={item.name}>
            <Link
              to={item.path}
              className={`flex items-center gap-3 px-3 py-2 rounded-md hover:bg-gray-700 transition-colors ${
                location.pathname === item.path ? "bg-gray-700 font-semibold" : ""
              }`}
            >
              {item.icon}
              {item.name}
            </Link>
          </li>
        ))}
      </ul>
    </aside>
  );
}
