// src/Sidebar.jsx
import { Stethoscope, Users, CalendarDays, FileText, X } from "lucide-react";
import { NavLink } from "react-router-dom";

export default function Sidebar({ sidebarOpen, setSidebarOpen }) {
  const menuItems = [
    { name: "Doctors", icon: <Stethoscope className="w-5 h-5" />, path: "/doctors" },
    { name: "Patients", icon: <Users className="w-5 h-5" />, path: "/patients" },
    { name: "Appointments", icon: <CalendarDays className="w-5 h-5" />, path: "/appointments" },
    { name: "Prescriptions", icon: <FileText className="w-5 h-5" />, path: "/prescriptions" },
  ];

  return (
    <>
      {/* Overlay for mobile */}
      <div
        className={`fixed inset-0 bg-black bg-opacity-50 z-40 transition-opacity md:hidden ${
          sidebarOpen ? "opacity-100" : "opacity-0 pointer-events-none"
        }`}
        onClick={() => setSidebarOpen(false)}
      ></div>

      <aside
        className={`fixed z-50 left-0 top-0 h-full w-64 bg-gray-800 text-gray-100 p-5 transform transition-transform md:translate-x-0 ${
          sidebarOpen ? "translate-x-0" : "-translate-x-full"
        }`}
      >
        {/* Close button for mobile */}
        <div className="flex justify-end md:hidden mb-4">
          <button onClick={() => setSidebarOpen(false)}>
            <X className="w-6 h-6 hover:text-red-500 transition-colors" />
          </button>
        </div>

        <ul className="space-y-4">
          {menuItems.map((item) => (
            <li key={item.name}>
              <NavLink
                to={item.path}
                className={({ isActive }) =>
                  `flex items-center gap-3 px-3 py-2 rounded-lg transition-colors ${
                    isActive ? "bg-blue-600 text-white" : "hover:bg-gray-700"
                  }`
                }
                onClick={() => setSidebarOpen(false)} // close sidebar on mobile click
              >
                {item.icon}
                <span>{item.name}</span>
              </NavLink>
            </li>
          ))}
        </ul>
      </aside>
    </>
  );
}
