import React, { useState, useEffect } from "react";
import axios from "axios";

export default function DoctorsPage() {
  const [doctors, setDoctors] = useState([]);
  const [loadingDoctors, setLoadingDoctors] = useState(true);
  const [doctorsError, setDoctorsError] = useState(null);

  const token = localStorage.getItem("token"); // Login এ save করা token

  useEffect(() => {
    if (!token) {
      // Token না থাকলে Login page এ redirect
      window.location.href = "/login";
      return;
    }

    const fetchDoctors = async () => {
      try {
        const response = await axios.get(
          "http://127.0.0.1:8000/api/doctors/",
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        setDoctors(response.data);
        setLoadingDoctors(false);
      } catch (err) {
        setDoctorsError(err.response?.data?.detail || err.message);
        setLoadingDoctors(false);
      }
    };

    fetchDoctors();
  }, [token]);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Doctors</h1>

      {loadingDoctors && <p>Loading doctors...</p>}
      {doctorsError && <p className="text-red-500">{doctorsError}</p>}

      {doctors.length > 0 && (
        <table className="w-full border">
          <thead>
            <tr>
              <th className="border p-2">ID</th>
              <th className="border p-2">Name</th>
              <th className="border p-2">Specialization</th>
              <th className="border p-2">BMDC No</th>
              <th className="border p-2">Verified</th>
            </tr>
          </thead>
          <tbody>
            {doctors.map((doctor) => (
              <tr key={doctor.id}>
                <td className="border p-2">{doctor.id}</td>
                <td className="border p-2">{doctor.name}</td>
                <td className="border p-2">{doctor.specialization}</td>
                <td className="border p-2">{doctor.bmdc_no}</td>
                <td className="border p-2">{doctor.is_verified ? "Yes" : "No"}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
