import React, { useState, useEffect } from "react";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";

import Dashboard from "./components/Dashboard";
import Home from "./pages/Home";
import Doctors from "./pages/Doctors";
import Patients from "./pages/Patients";
import Appointments from "./pages/Appointments";
import Prescriptions from "./pages/Prescriptions";
import Auth from "./components/Auth"; // Login/Register component
import LandingPage from "./pages/LandingPage"; // Landing page
import { UserProvider } from "./components/UserContext";

export default function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [role, setRole] = useState("");
  const [username, setUsername] = useState("");
  const [page, setPage] = useState("landing"); // landing | auth

  // -------------------------
  // Check authentication on load
  // -------------------------
  useEffect(() => {
    const token = localStorage.getItem("access"); // match your login page
    const savedRole = localStorage.getItem("role");
    const savedUsername = localStorage.getItem("username");

    if (token) {
      setIsAuthenticated(true);
      setRole(savedRole || "");
      setUsername(savedUsername || "");
    }
  }, []);

  // -------------------------
  // Logout handler
  // -------------------------
  const handleLogout = () => {
    localStorage.clear();
    setIsAuthenticated(false);
    setRole("");
    setUsername("");
    setPage("landing"); // redirect to landing page after logout
  };

  // -------------------------
  // Role-based dashboard URL
  // -------------------------
  const getDashboardUrl = () => {
    switch (role) {
      case "doctor":
        return "/doctors";
      case "patient":
        return "/patients";
      case "admin":
        return "/doctors"; // or "/home" with admin panels
      default:
        return "/home";
    }
  };

  // -------------------------
  // Authenticated app with dashboard
  // -------------------------
  if (isAuthenticated) {
    return (
      <UserProvider>
        <BrowserRouter>
          <Dashboard onLogout={handleLogout} username={username} role={role}>
            <Routes>
              {/* Default route */}
              <Route path="/" element={<Navigate to={getDashboardUrl()} replace />} />

              {/* Main pages with props */}
              <Route
                path="/home"
                element={<Home username={username} role={role} />}
              />
              <Route
                path="/doctors"
                element={<Doctors username={username} role={role} />}
              />
              <Route
                path="/patients"
                element={<Patients username={username} role={role} />}
              />
              <Route
                path="/appointments"
                element={<Appointments username={username} role={role} />}
              />
              <Route
                path="/prescriptions"
                element={<Prescriptions username={username} role={role} />}
              />

              {/* Catch-all unknown routes */}
              <Route path="*" element={<Navigate to={getDashboardUrl()} replace />} />
            </Routes>
          </Dashboard>
        </BrowserRouter>
      </UserProvider>
    );
  }

  // -------------------------
  // Unauthenticated users: landing or login/register
  // -------------------------
  return page === "landing" ? (
    <LandingPage goToLogin={() => setPage("auth")} />
  ) : (
    <Auth
      setIsAuthenticated={setIsAuthenticated}
      setRole={setRole}
      setUsername={setUsername}
    />
  );
}
