// src/api.js
import axios from "axios";

const API_URL = "http://127.0.0.1:8000"; // your Django backend URL

const axiosInstance = axios.create({
  baseURL: `${API_URL}/api/`,
  headers: {
    "Content-Type": "application/json",
  },
});

export default axiosInstance;
