// src/pages/Appointments.jsx
import { useEffect, useState } from "react";
import axiosInstance from "../api";
import { getAuthHeader } from "../auth";

export default function Appointments({ tenantSlug = "" }) {
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchAppointments();
  }, []);

  const fetchAppointments = async () => {
    try {
      setLoading(true);
      const url = tenantSlug
        ? `${tenantSlug}/api/appointments/`
        : "api/appointments/";
      const res = await axiosInstance.get(url, { headers: getAuthHeader() });
      setAppointments(res.data);
    } catch (err) {
      console.error("Error fetching appointments:", err);
      setAppointments([]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-6 text-gray-800">Appointments</h1>

      {loading ? (
        <div className="flex justify-center items-center h-64">
          <div className="animate-spin rounded-full h-12 w-12 border-b-4 border-yellow-500"></div>
        </div>
      ) : appointments.length === 0 ? (
        <p className="text-gray-500 text-lg">No appointments found.</p>
      ) : (
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white rounded-lg shadow-md border border-gray-200">
            <thead className="bg-yellow-600 text-white">
              <tr>
                <th className="px-6 py-3 text-left font-medium">ID</th>
                <th className="px-6 py-3 text-left font-medium">Patient</th>
                <th className="px-6 py-3 text-left font-medium">Doctor</th>
                <th className="px-6 py-3 text-left font-medium">Date & Time</th>
                <th className="px-6 py-3 text-left font-medium">Status</th>
              </tr>
            </thead>
            <tbody>
              {appointments.map((app) => (
                <tr
                  key={app.id}
                  className="border-b hover:bg-yellow-50 transition-colors"
                >
                  <td className="px-6 py-3">{app.id}</td>
                  <td className="px-6 py-3">{app.patient_name}</td>
                  <td className="px-6 py-3">{app.doctor_name}</td>
                  <td className="px-6 py-3">{new Date(app.date_time).toLocaleString()}</td>
                  <td className="px-6 py-3">{app.status}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}
