// src/pages/Prescriptions.jsx
import { useEffect, useState } from "react";
import axiosInstance from "../api";
import { getAuthHeader } from "../auth";

export default function Prescriptions() {
  const [prescriptions, setPrescriptions] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchPrescriptions();
  }, []);

  const fetchPrescriptions = async () => {
    try {
      setLoading(true);
      const res = await axiosInstance.get("api/prescriptions/", { headers: getAuthHeader() });
      setPrescriptions(res.data);
    } catch (err) {
      console.error("Error fetching prescriptions:", err.response || err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold mb-6 text-gray-800">Prescriptions</h1>

      {loading ? (
        <div className="flex justify-center items-center h-64">
          <div className="animate-spin rounded-full h-12 w-12 border-t-4 border-purple-500"></div>
        </div>
      ) : prescriptions.length === 0 ? (
        <p className="text-gray-500 text-lg">No prescriptions found.</p>
      ) : (
        <div className="overflow-x-auto rounded-lg shadow-lg bg-white border border-gray-200">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-purple-600 text-white">
              <tr>
                <th className="px-6 py-3 text-left text-sm font-semibold uppercase tracking-wider">ID</th>
                <th className="px-6 py-3 text-left text-sm font-semibold uppercase tracking-wider">Patient</th>
                <th className="px-6 py-3 text-left text-sm font-semibold uppercase tracking-wider">Doctor</th>
                <th className="px-6 py-3 text-left text-sm font-semibold uppercase tracking-wider">Medication</th>
                <th className="px-6 py-3 text-left text-sm font-semibold uppercase tracking-wider">Dosage</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {prescriptions.map((pres) => (
                <tr key={pres.id} className="hover:bg-purple-50 transition-colors duration-200">
                  <td className="px-6 py-4 text-sm text-gray-700">{pres.id}</td>
                  <td className="px-6 py-4 text-sm text-gray-900">{pres.patient_name}</td>
                  <td className="px-6 py-4 text-sm text-gray-700">{pres.doctor_name}</td>
                  <td className="px-6 py-4 text-sm text-gray-700">{pres.medication}</td>
                  <td className="px-6 py-4 text-sm text-gray-700">{pres.dosage}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}
