// src/pages/Doctors.jsx
import { useEffect, useState } from "react";
import axiosInstance from "../api";
import { getAuthHeader } from "../auth";

export default function Doctors() {
  const [doctors, setDoctors] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDoctors();
  }, []);

  const fetchDoctors = async () => {
    try {
      setLoading(true);
      const res = await axiosInstance.get("api/doctors/", { headers: getAuthHeader() });
      setDoctors(res.data);
    } catch (err) {
      console.error("Error fetching doctors:", err.response || err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold mb-6 text-gray-800">Doctors</h1>

      {loading ? (
        <div className="flex justify-center items-center h-64">
          <div className="animate-spin rounded-full h-12 w-12 border-t-4 border-blue-500"></div>
        </div>
      ) : doctors.length === 0 ? (
        <p className="text-gray-500 text-lg">No doctors found.</p>
      ) : (
        <div className="overflow-x-auto rounded-lg shadow-lg bg-white border border-gray-200">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-blue-600 text-white">
              <tr>
                <th className="px-6 py-3 text-left text-sm font-semibold uppercase tracking-wider">ID</th>
                <th className="px-6 py-3 text-left text-sm font-semibold uppercase tracking-wider">Name</th>
                <th className="px-6 py-3 text-left text-sm font-semibold uppercase tracking-wider">Specialization</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {doctors.map((doc) => (
                <tr key={doc.id} className="hover:bg-blue-50 transition-colors duration-200">
                  <td className="px-6 py-4 text-sm text-gray-700">{doc.id}</td>
                  <td className="px-6 py-4 text-sm text-gray-900">{doc.name}</td>
                  <td className="px-6 py-4 text-sm text-gray-700">{doc.specialization}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}
