// src/pages/Prescriptions.jsx
import { useEffect, useState } from "react";
import axiosInstance from "../api";
import { getAuthHeader } from "../auth";

export default function Prescriptions() {
  const [prescriptions, setPrescriptions] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchPrescriptions();
  }, []);

  const fetchPrescriptions = async () => {
    try {
      setLoading(true);
      const res = await axiosInstance.get("prescriptions/", { headers: getAuthHeader() });
      setPrescriptions(res.data);
    } catch (error) {
      console.error("Error fetching prescriptions", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h1 className="text-2xl font-bold mb-4">Prescriptions</h1>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <table className="table-auto w-full bg-white rounded-lg shadow">
          <thead className="bg-gray-200">
            <tr>
              <th className="px-4 py-2">ID</th>
              <th className="px-4 py-2">Patient</th>
              <th className="px-4 py-2">Doctor</th>
              <th className="px-4 py-2">Medicine</th>
              <th className="px-4 py-2">Instructions</th>
            </tr>
          </thead>
          <tbody>
            {prescriptions.map((p) => (
              <tr key={p.id} className="text-center border-t">
                <td className="px-4 py-2">{p.id}</td>
                <td className="px-4 py-2">{p.patient_name}</td>
                <td className="px-4 py-2">{p.doctor_name}</td>
                <td className="px-4 py-2">{p.medicine}</td>
                <td className="px-4 py-2">{p.instructions}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
