import { useEffect, useState, useMemo } from "react";

function errText(err) {
  try {
    if (!err?.message) return "Network error";
    return err.message;
  } catch {
    return "Request failed.";
  }
}

export default function DoctorsPage({ username, role }) {
  const [doctors, setDoctors] = useState([]);
  const [loadingDoctors, setLoadingDoctors] = useState(true);
  const [doctorsError, setDoctorsError] = useState(null);

  const [users, setUsers] = useState([]);
  const [loadingUsers, setLoadingUsers] = useState(false);
  const [usersError, setUsersError] = useState(null);

  const [q, setQ] = useState("");
  const [userId, setUserId] = useState("");
  const [formData, setFormData] = useState({
    specialization: "",
    name: "",
    email: "",
    phone: "",
    bmdc_no: "",
  });

  const [submitError, setSubmitError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [submitting, setSubmitting] = useState(false);

  const token = localStorage.getItem("access");
  const authHeader = {
    "Authorization": `Bearer ${token}`,
    "Content-Type": "application/json",
  };

  const fetchDoctors = async () => {
    if (!token) return setDoctorsError("No access token found.");
    setLoadingDoctors(true);
    setDoctorsError(null);
    try {
      const res = await fetch("http://localhost:8000/api/doctors/", { headers: authHeader });
      if (!res.ok) throw new Error(`Failed to fetch doctors: ${res.status}`);
      const data = await res.json();
      setDoctors(data);
    } catch (err) {
      setDoctorsError(errText(err));
    } finally {
      setLoadingDoctors(false);
    }
  };

  const fetchUsers = async () => {
    if (role !== "admin" || !token) return;
    setLoadingUsers(true);
    setUsersError(null);
    try {
      let url = "http://localhost:8000/api/users/";
      const allUsers = [];
      while (url) {
        const res = await fetch(url, { headers: authHeader });
        if (!res.ok) throw new Error("Failed to fetch users");
        const data = await res.json();
        allUsers.push(...(Array.isArray(data) ? data : data.results || []));
        url = data?.next || null;
      }
      setUsers(allUsers);
    } catch (err) {
      setUsersError(errText(err));
    } finally {
      setLoadingUsers(false);
    }
  };

  useEffect(() => { fetchDoctors(); }, [token]);
  useEffect(() => { fetchUsers(); }, [role, token]);

  const doctorUserIds = useMemo(() => doctors.map(d => d.user), [doctors]);

  const filteredUsers = useMemo(() => {
    if (!q) return users;
    const search = q.toLowerCase();
    return users.filter(
      u => (u.username?.toLowerCase().includes(search)) ||
           (u.email?.toLowerCase().includes(search)) ||
           String(u.id).includes(search)
    );
  }, [q, users]);

  const displayedDoctors = useMemo(() => {
    if (role === "admin" || role === "management") return doctors;
    return doctors.filter(d => d.is_verified);
  }, [doctors, role]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitError(null);
    setSuccess(null);

    if (!userId || !formData.specialization.trim() || !formData.bmdc_no.trim()) {
      setSubmitError("User ID, Specialization এবং BMDC Number প্রয়োজন।");
      return;
    }
    if (doctorUserIds.includes(Number(userId))) {
      setSubmitError("This user is already registered as a doctor.");
      return;
    }

    try {
      setSubmitting(true);
      const payload = {
        user_id: Number(userId),
        specialization: formData.specialization.trim(),
        name: formData.name || undefined,
        email: formData.email || undefined,
        phone: formData.phone || "",
        bmdc_no: formData.bmdc_no || "",
      };
      const res = await fetch("http://localhost:8000/api/doctors/register/", {
        method: "POST",
        headers: authHeader,
        body: JSON.stringify(payload),
      });
      if (!res.ok) {
        const data = await res.json().catch(() => null);
        throw new Error(data?.error || "Doctor registration failed");
      }
      setSuccess("Doctor added successfully!");
      setFormData({ specialization: "", name: "", email: "", phone: "", bmdc_no: "" });
      setUserId("");
      fetchDoctors();
    } catch (err) { setSubmitError(errText(err)); }
    finally { setSubmitting(false); }
  };

  // Admin actions
  const handleApprove = async (id) => {
    try {
      const res = await fetch(`http://localhost:8000/api/doctors/${id}/approve/`, {
        method: "PATCH",
        headers: authHeader,
      });
      if (!res.ok) throw new Error("Failed to approve doctor");
      fetchDoctors();
    } catch (err) {
      alert(errText(err));
    }
  };

  const handleReject = async (id) => {
    try {
      const res = await fetch(`http://localhost:8000/api/doctors/${id}/reject/`, {
        method: "PATCH",
        headers: authHeader,
      });
      if (!res.ok) throw new Error("Failed to reject doctor");
      fetchDoctors();
    } catch (err) {
      alert(errText(err));
    }
  };

  const handleInlineUpdate = async (id, field, value) => {
    try {
      const payload = { [field]: value };
      const res = await fetch(`http://localhost:8000/api/doctors/${id}/`, {
        method: "PATCH",
        headers: authHeader,
        body: JSON.stringify(payload),
      });
      if (!res.ok) throw new Error("Update failed");
      fetchDoctors();
    } catch (err) {
      alert(errText(err));
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-6">Doctors</h1>

      {(role === "admin" || role === "management") && (
        <div className="mb-6 p-6 bg-white rounded shadow-md max-w-md">
          <h2 className="text-xl font-bold mb-4">Add Doctor</h2>
          {submitError && <p className="text-red-500 mb-2">{submitError}</p>}
          {success && <p className="text-green-500 mb-2">{success}</p>}

          <input
            type="text"
            placeholder="Search users..."
            value={q}
            onChange={e => setQ(e.target.value)}
            className="border p-2 w-full rounded mb-2"
          />
          <select
            value={userId}
            onChange={e => setUserId(e.target.value)}
            className="border p-2 w-full rounded mb-2"
            disabled={loadingUsers}
          >
            <option value="">{loadingUsers ? "Loading users..." : "Select a user"}</option>
            {filteredUsers.map(u => (
              <option key={u.id} value={u.id} disabled={doctorUserIds.includes(u.id)}>
                {u.username || u.email || `user#${u.id}`} 
                {doctorUserIds.includes(u.id) ? " (Already a doctor)" : ""}
              </option>
            ))}
          </select>

          <input
            type="text"
            placeholder="Specialization"
            value={formData.specialization}
            onChange={e => setFormData({...formData, specialization: e.target.value})}
            className="border p-2 w-full rounded mb-2"
          />
          <input
            type="text"
            placeholder="Name (optional)"
            value={formData.name}
            onChange={e => setFormData({...formData, name: e.target.value})}
            className="border p-2 w-full rounded mb-2"
          />
          <input
            type="text"
            placeholder="BMDC Number"
            value={formData.bmdc_no}
            onChange={e => setFormData({...formData, bmdc_no: e.target.value})}
            className="border p-2 w-full rounded mb-2"
          />
          <input
            type="email"
            placeholder="Email (optional)"
            value={formData.email}
            onChange={e => setFormData({...formData, email: e.target.value})}
            className="border p-2 w-full rounded mb-2"
          />
          <input
            type="text"
            placeholder="Phone (optional)"
            value={formData.phone}
            onChange={e => setFormData({...formData, phone: e.target.value})}
            className="border p-2 w-full rounded mb-2"
          />

          <button
            type="submit"
            onClick={handleSubmit}
            disabled={!userId || !formData.specialization || !formData.bmdc_no || submitting}
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 disabled:opacity-60 w-full"
          >
            {submitting ? "Adding..." : "Add Doctor"}
          </button>
        </div>
      )}

      <div className="overflow-x-auto bg-white rounded shadow-md p-4">
        <h2 className="text-xl font-bold mb-4">Doctors List</h2>
        {loadingDoctors ? (
          <p>Loading doctors...</p>
        ) : doctorsError ? (
          <p className="text-red-500">{doctorsError}</p>
        ) : displayedDoctors.length === 0 ? (
          <p>No doctors found.</p>
        ) : (
          <table className="min-w-full bg-white border border-gray-200">
            <thead className="bg-blue-600 text-white">
              <tr>
                <th className="px-6 py-3 text-left">ID</th>
                <th className="px-6 py-3 text-left">Name</th>
                <th className="px-6 py-3 text-left">Specialization</th>
                <th className="px-6 py-3 text-left">BMDC No</th>
                <th className="px-6 py-3 text-left">Email</th>
                <th className="px-6 py-3 text-left">Phone</th>
                <th className="px-6 py-3 text-left">Status</th>
                {role === "admin" && <th className="px-6 py-3 text-left">Actions</th>}
              </tr>
            </thead>
            <tbody>
              {displayedDoctors.map(doc => (
                <tr key={doc.id} className="border-b hover:bg-blue-50">
                  <td className="px-6 py-3">{doc.id}</td>
                  <td className="px-6 py-3">
                    {role === "admin" ? (
                      <input
                        type="text"
                        defaultValue={doc.name}
                        onBlur={e => handleInlineUpdate(doc.id, "name", e.target.value)}
                        className="border p-1 w-full rounded"
                      />
                    ) : doc.name}
                  </td>
                  <td className="px-6 py-3">
                    {role === "admin" ? (
                      <input
                        type="text"
                        defaultValue={doc.specialization}
                        onBlur={e => handleInlineUpdate(doc.id, "specialization", e.target.value)}
                        className="border p-1 w-full rounded"
                      />
                    ) : doc.specialization}
                  </td>
                  <td className="px-6 py-3">{doc.bmdc_no}</td>
                  <td className="px-6 py-3">{doc.email}</td>
                  <td className="px-6 py-3">{doc.phone}</td>
                  <td className="px-6 py-3">
                    {doc.is_verified ? <span className="text-green-600">Approved</span> : <span className="text-yellow-600">Pending</span>}
                  </td>
                  {role === "admin" && (
                    <td className="px-6 py-3 space-x-2">
                      {!doc.is_verified && (
                        <>
                          <button
                            onClick={() => handleApprove(doc.id)}
                            className="bg-green-600 text-white px-2 py-1 rounded hover:bg-green-700"
                          >
                            Approve
                          </button>
                          <button
                            onClick={() => handleReject(doc.id)}
                            className="bg-red-600 text-white px-2 py-1 rounded hover:bg-red-700"
                          >
                            Reject
                          </button>
                        </>
                      )}
                    </td>
                  )}
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}
