import { useEffect, useMemo, useState } from "react";
import axiosInstance from "../api";
import { getAuthHeader } from "../auth";

export default function Doctors() {
  const [doctors, setDoctors] = useState([]);
  const [users, setUsers] = useState([]);
  const [usersLoading, setUsersLoading] = useState(false);

  const [loading, setLoading] = useState(true);
  const [q, setQ] = useState(""); // user search
  const [userId, setUserId] = useState(""); // <-- new (required by backend)
  const [formData, setFormData] = useState({
    specialization: "",
    phone: "",
    // name/email থাকলে পাঠাতে পারেন, serializer অটো-ফিলও করে
    name: "",
    email: "",
  });

  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  // -------- Helpers --------
  const extractList = (data) => (Array.isArray(data) ? data : data?.results || []);
  const extractErr = (err) => {
    const d = err?.response?.data;
    if (!d) return "Request failed.";
    return typeof d === "string" ? d : JSON.stringify(d);
  };

  // -------- Fetch Doctors (pagination-safe) --------
  const fetchDoctors = async () => {
    try {
      setLoading(true);
      // NOTE: baseURL এ /api/ আছে, তাই এখানে /api দেবেন না
      const res = await axiosInstance.get("doctors/", { headers: getAuthHeader() });
      setDoctors(extractList(res.data));
    } catch (err) {
      console.error("Error fetching doctors:", err);
      setError(extractErr(err));
    } finally {
      setLoading(false);
    }
  };

  // -------- Fetch Users for user_id select --------
  const fetchUsers = async () => {
    try {
      setUsersLoading(true);
      let url = "users/";
      const all = [];
      while (url) {
        const { data } = await axiosInstance.get(url, { headers: getAuthHeader() });
        all.push(...extractList(data));
        if (!Array.isArray(data) && data?.next) {
          try {
            const u = new URL(data.next);
            url = u.pathname.replace(/^\/api\//, "") + (u.search || "");
          } catch {
            url = data.next; // already relative
          }
        } else {
          url = null;
        }
      }
      setUsers(all);
    } catch (e) {
      console.error("Error fetching users:", e);
      setError("Failed to load users.");
    } finally {
      setUsersLoading(false);
    }
  };

  useEffect(() => {
    fetchDoctors();
    fetchUsers();
  }, []);

  const filteredUsers = useMemo(() => {
    if (!q) return users;
    const s = q.toLowerCase();
    return users.filter(
      (u) =>
        (u.username && u.username.toLowerCase().includes(s)) ||
        (u.email && u.email.toLowerCase().includes(s)) ||
        String(u.id).includes(s)
    );
  }, [q, users]);

  // -------- Form handlers --------
  const handleChange = (e) => {
    setFormData((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setSuccess(null);

    if (!userId || !formData.specialization.trim()) {
      setError("User ID এবং Specialization প্রয়োজন।");
      return;
    }

    try {
      // NOTE: baseURL: .../api/ ⇒ এখানে /api দেবেন না
      const payload = {
        user_id: Number(userId),                 // <-- required by backend
        specialization: formData.specialization.trim(),
        phone: formData.phone || "",
        // name/email পাঠাতে পারেন (optional)
        name: formData.name || undefined,
        email: formData.email || undefined,
      };

      const res = await axiosInstance.post("doctors/register/", payload, {
        headers: getAuthHeader(),
      });

      setSuccess("Doctor added successfully!");
      setFormData({ name: "", specialization: "", email: "", phone: "" });
      setUserId("");
      await fetchDoctors();
    } catch (err) {
      console.error("Failed to add doctor:", err);
      setError(extractErr(err));
    }
  };

  // -------- UI --------
  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-6 text-gray-800">Doctors</h1>

      {/* Add Doctor Form */}
      <form onSubmit={handleSubmit} className="mb-6 space-y-4">
        {error && <p className="text-red-500 break-words">{error}</p>}
        {success && <p className="text-green-500">{success}</p>}

        {/* User search + select (for user_id) */}
        <div>
          <label className="block font-medium mb-1">Search users</label>
          <input
            type="text"
            placeholder="username/email/id"
            value={q}
            onChange={(e) => setQ(e.target.value)}
            className="border p-2 rounded w-full mb-2"
          />
          <select
            value={userId}
            onChange={(e) => setUserId(e.target.value)}
            className="border p-2 rounded w-full"
            disabled={usersLoading}
          >
            <option value="">{usersLoading ? "Loading users..." : "Select a user"}</option>
            {filteredUsers.map((u) => (
              <option key={u.id} value={u.id}>
                {(u.username || u.email || "user")} #{u.id}
              </option>
            ))}
          </select>
        </div>

        <input
          type="text"
          name="specialization"
          placeholder="Specialization"
          value={formData.specialization}
          onChange={handleChange}
          className="border p-2 rounded w-full"
        />

        {/* optional fields (serializer অটো-ফিলও করে) */}
        <input
          type="text"
          name="name"
          placeholder="Doctor Name (optional)"
          value={formData.name}
          onChange={handleChange}
          className="border p-2 rounded w-full"
        />
        <input
          type="email"
          name="email"
          placeholder="Email (optional)"
          value={formData.email}
          onChange={handleChange}
          className="border p-2 rounded w-full"
        />
        <input
          type="text"
          name="phone"
          placeholder="Phone (optional)"
          value={formData.phone}
          onChange={handleChange}
          className="border p-2 rounded w-full"
        />

        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded disabled:opacity-60"
          disabled={!userId || !formData.specialization.trim()}
        >
          Add Doctor
        </button>
      </form>

      {/* Doctors Table */}
      {loading ? (
        <div className="flex justify-center items-center h-64">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500"></div>
        </div>
      ) : doctors.length === 0 ? (
        <p className="text-gray-500 text-lg">No doctors found.</p>
      ) : (
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white rounded-lg shadow-md border border-gray-200">
            <thead className="bg-blue-600 text-white">
              <tr>
                <th className="px-6 py-3 text-left font-medium">ID</th>
                <th className="px-6 py-3 text-left font-medium">Name</th>
                <th className="px-6 py-3 text-left font-medium">Specialization</th>
                <th className="px-6 py-3 text-left font-medium">Email</th>
                <th className="px-6 py-3 text-left font-medium">Phone</th>
              </tr>
            </thead>
            <tbody>
              {doctors.map((doc) => (
                <tr key={doc.id} className="border-b hover:bg-blue-50 transition-colors">
                  <td className="px-6 py-3">{doc.id}</td>
                  <td className="px-6 py-3">{doc.name}</td>
                  <td className="px-6 py-3">{doc.specialization}</td>
                  <td className="px-6 py-3">{doc.email}</td>
                  <td className="px-6 py-3">{doc.phone}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}
