// src/pages/Prescriptions.jsx
import { useEffect, useState } from "react";
import axiosInstance from "../api";
import { getAuthHeader } from "../auth";

export default function Prescriptions({ tenantSlug = "" }) {
  const [prescriptions, setPrescriptions] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchPrescriptions();
  }, []);

  const fetchPrescriptions = async () => {
    try {
      setLoading(true);
      const url = tenantSlug
        ? `${tenantSlug}/api/prescriptions/`
        : "api/prescriptions/";
      const res = await axiosInstance.get(url, { headers: getAuthHeader() });
      setPrescriptions(res.data);
    } catch (err) {
      console.error("Error fetching prescriptions:", err);
      setPrescriptions([]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-6 text-gray-800">Prescriptions</h1>

      {loading ? (
        <div className="flex justify-center items-center h-64">
          <div className="animate-spin rounded-full h-12 w-12 border-b-4 border-purple-500"></div>
        </div>
      ) : prescriptions.length === 0 ? (
        <p className="text-gray-500 text-lg">No prescriptions found.</p>
      ) : (
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white rounded-lg shadow-md border border-gray-200">
            <thead className="bg-purple-600 text-white">
              <tr>
                <th className="px-6 py-3 text-left font-medium">ID</th>
                <th className="px-6 py-3 text-left font-medium">Patient</th>
                <th className="px-6 py-3 text-left font-medium">Doctor</th>
                <th className="px-6 py-3 text-left font-medium">Medication</th>
                <th className="px-6 py-3 text-left font-medium">Dosage</th>
              </tr>
            </thead>
            <tbody>
              {prescriptions.map((pr) => (
                <tr
                  key={pr.id}
                  className="border-b hover:bg-purple-50 transition-colors"
                >
                  <td className="px-6 py-3">{pr.id}</td>
                  <td className="px-6 py-3">{pr.patient_name}</td>
                  <td className="px-6 py-3">{pr.doctor_name}</td>
                  <td className="px-6 py-3">{pr.medication}</td>
                  <td className="px-6 py-3">{pr.dosage}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}
