import { useState, useEffect } from "react";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";

import Dashboard from "./components/Dashboard";
import Home from "./pages/Home";
import Doctors from "./pages/Doctors";
import Patients from "./pages/Patients";
import Appointments from "./pages/Appointments";
import Prescriptions from "./pages/Prescriptions";
import Auth from "./components/auth"; // Ensure filename matches exactly (auth.jsx)

export default function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [role, setRole] = useState(""); // Store logged-in user role

  // -------------------------
  // Check if user is already logged in
  // -------------------------
  useEffect(() => {
    const token = localStorage.getItem("access_token");
    const userRole = localStorage.getItem("role"); // optional, if you store role
    if (token) {
      setIsAuthenticated(true);
      setRole(userRole || "");
    }
  }, []);

  // -------------------------
  // Get dashboard URL from localStorage after login
  // -------------------------
  const dashboardUrl = localStorage.getItem("dashboard_url") || "/home";

  // -------------------------
  // Render main page inside dashboard
  // -------------------------
  const renderPage = (page) => {
    switch (page) {
      case "home":
        return <Home />;
      case "doctors":
        return <Doctors />;
      case "patients":
        return <Patients />;
      case "appointments":
        return <Appointments />;
      case "prescriptions":
        return <Prescriptions />;
      default:
        return <Home />;
    }
  };

  // -------------------------
  // If not authenticated, show login/register
  // -------------------------
  if (!isAuthenticated) {
    return <Auth setIsAuthenticated={setIsAuthenticated} setRole={setRole} />;
  }

  // -------------------------
  // Authenticated routes inside dashboard
  // -------------------------
  return (
    <BrowserRouter>
      <Dashboard setIsAuthenticated={setIsAuthenticated}>
        <Routes>
          <Route path="/" element={renderPage("home")} />
          <Route path="/home" element={<Home />} />
          <Route path="/doctors" element={<Doctors />} />
          <Route path="/patients" element={<Patients />} />
          <Route path="/appointments" element={<Appointments />} />
          <Route path="/prescriptions" element={<Prescriptions />} />

          {/* Redirect to dashboard URL based on role */}
          <Route path="/dashboard" element={<Navigate to={dashboardUrl} replace />} />

          {/* Catch-all: redirect unknown routes */}
          <Route path="*" element={<Navigate to={dashboardUrl} replace />} />
        </Routes>
      </Dashboard>
    </BrowserRouter>
  );
}
