import React, { useEffect, useState } from "react";
import axiosInstance from "../api"; // Make sure axiosInstance has baseURL set and JWT token if needed

function StatCard({ title, count, bg }) {
  return (
    <div
      className={`${bg} text-white p-6 rounded-lg shadow-md transform hover:scale-105 transition-transform`}
    >
      <h2 className="text-xl font-semibold">{title}</h2>
      <p className="text-3xl font-bold mt-2">{count}</p>
    </div>
  );
}

export default function Home() {
  const [stats, setStats] = useState({
    doctors: 0,
    patients: 0,
    appointments: 0,
    prescriptions: 0,
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchStats = async () => {
    try {
      setLoading(true);
      const response = await axiosInstance.get("/api/stats/");
      setStats(response.data);
    } catch (err) {
      console.error("Failed to fetch stats:", err);
      setError("Failed to load statistics.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchStats();
  }, []);

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  if (error) {
    return <p className="text-red-500 text-center mt-6">{error}</p>;
  }

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">
        Welcome to Doctor APS
      </h1>
      <p className="text-gray-600 mb-6">
        Manage your doctors, patients, appointments, and prescriptions.
      </p>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <StatCard title="Doctors" count={stats.doctors} bg="bg-blue-600" />
        <StatCard title="Patients" count={stats.patients} bg="bg-green-600" />
        <StatCard title="Appointments" count={stats.appointments} bg="bg-yellow-500" />
        <StatCard title="Prescriptions" count={stats.prescriptions} bg="bg-red-500" />
      </div>
    </div>
  );
}
